
    function generarPDF() {
        const { jsPDF } = window.jspdf;
        const doc = new jsPDF();

        // Agregar logo en el área de los datos a mano
        var logoBase64 = "data:image/jpeg;base64,";  // Coloca aquí la cadena base64 del logo
        doc.addImage(logoBase64, 'PNG', 100, 180, 50, 50);  // Ajustar tamaño y posición del logo

        // Reducir tamaño de letra para fecha y orden de trabajo
        doc.setFontSize(10);
        doc.text("Fecha: ____________________", 20, 20);
        doc.text("Orden de Trabajo: ____________________", 140, 20); // A la derecha

        // Recuperar la imagen pegada desde el portapapeles
        var imagen = localStorage.getItem('uploadedImage');
        if (imagen) {
            var img = new Image();
            img.src = imagen;
            img.onload = function() {
                doc.addImage(img, 'PNG', 10, 30, 180, 100); // Agregar imagen al PDF

                // Añadir los datos al PDF
                agregarDatosPDF(doc);
            };
        } else {
            agregarDatosPDF(doc);
        }
    }

    function agregarDatosPDF(doc) {
        // Recuperar los datos del formulario
        var cliente = document.getElementById('cliente').value;
        var campo = document.getElementById('campo').value;
        var lote = document.getElementById('lote').value;
        var totalHectareas = document.getElementById('totalHectareas').value;
        var caudalHectarea = document.getElementById('caudalHectarea').value;
        var piloto = document.getElementById('piloto').value;
        var equipoTierra = document.getElementById('equipoTierra').value;

        // Productos
        var productos = [];
        for (var i = 1; i <= 6; i++) {
            var producto = document.getElementById('producto' + i).value;
            var dosis = document.getElementById('dosis' + i).value;
            if (producto && dosis) {
                productos.push({ producto, dosis });
            }
        }

        // Añadir datos al PDF
        doc.setFontSize(12);
        doc.text(`Cliente: ${cliente}`, 20, 140);
        doc.text(`Cantidad Total Hectáreas: ${totalHectareas}`, 140, 140); // Derecha
        doc.text(`Campo: ${campo}`, 20, 150);
        doc.text(`Lote: ${lote}`, 20, 160);
        doc.text(`Caudal por Hectárea: ${caudalHectarea}`, 140, 160); // Derecha

        // Reducir tamaño de letra para piloto y equipo de tierra
        doc.setFontSize(10);
        doc.text(`Piloto: ${piloto}`, 20, 170);
        doc.text(`Equipo de Tierra: ${equipoTierra}`, 20, 180);

        // Añadir espacio para escribir a mano
        doc.text("Hora de Inicio: _______________", 20, 190);
        doc.text("Hora Final: ___________________", 20, 200);
        doc.text("Viento: ______________________", 20, 210);
        doc.text("Temperatura: ________________", 20, 220);
        doc.text("Humedad: ____________________", 20, 230);
        doc.text("Coordenadas: ________________", 20, 240);

        // Combustible utilizado
        doc.text("Combustible Utilizado: __________________", 20, 250);

        // Reducir tamaño de letra e interlineado para los productos
        doc.setFontSize(9);  // Tamaño más reducido
        productos.forEach((producto, index) => {
            doc.text(`Producto ${index + 1}: ${producto.producto} - Dosis: ${producto.dosis}`, 20, 260 + (index * 7));
        });

        // Guardar el PDF
        doc.save("aplicacion_aerea.pdf");
    }

    // Función para pegar la imagen usando Ctrl+V
    document.addEventListener('paste', function (event) {
        var items = (event.clipboardData || event.originalEvent.clipboardData).items;
        for (var index in items) {
            var item = items[index];
            if (item.kind === 'file') {
                var blob = item.getAsFile();
                var reader = new FileReader();
                reader.onload = function (event) {
                    // Mostrar la imagen pegada
                    var imgElement = document.createElement('img');
                    imgElement.src = event.target.result;
                    var imageContainer = document.getElementById('imageContainer');
                    imageContainer.innerHTML = ''; // Limpiar contenido anterior
                    imageContainer.appendChild(imgElement);

                    // Guardar la imagen en localStorage
                    localStorage.setItem('uploadedImage', event.target.result);
                };
                reader.readAsDataURL(blob);
            }
        }
    });

    // Función para reiniciar la página
    function reiniciar() {
        document.getElementById('datosForm').reset();
        localStorage.removeItem('uploadedImage');
        var imageContainer = document.getElementById('imageContainer');
        imageContainer.innerHTML = '<p>Pega una imagen aquí usando Ctrl + V</p>';
    }

    // Función para generar el PDF de cálculos de equipo de tierra
    function calculosEquipoTierra() {
        const { jsPDF } = window.jspdf;
        const doc = new jsPDF();

        var totalHectareas = parseFloat(document.getElementById('totalHectareas').value);
        var caudalHectarea = parseFloat(document.getElementById('caudalHectarea').value);
        var cantidadVuelos = parseInt(document.getElementById('cantidadVuelos').value);

        // Cálculo del caldo total por vuelo
        var caldoTotalPorVuelo = (caudalHectarea * totalHectareas) / cantidadVuelos;

        // Añadir productos y dosis por vuelo
        doc.setFontSize(12);
        doc.text("Cálculos equipo de tierra", 20, 20);
        doc.text(`Cantidad total de caldo por vuelo: ${caldoTotalPorVuelo.toFixed(2)} litros`, 20, 40);

        var productos = [];
        for (var i = 1; i <= 6; i++) {
            var producto = document.getElementById('producto' + i).value;
            var dosis = parseFloat(document.getElementById('dosis' + i).value);
            if (producto && dosis) {
                var productoPorVuelo = (dosis * totalHectareas) / cantidadVuelos;
                productos.push({ producto, productoPorVuelo });
                doc.text(`${producto}: ${productoPorVuelo.toFixed(2)} unidades por vuelo`, 20, 50 + (i * 10));
            }
        }

        // Guardar el PDF
        doc.save("calculos_equipo_tierra.pdf");
    }

